// export default Navbar;
import React, { useState, useEffect, useRef } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';

const Navbar = ({ user, onLogout }) => {
  const navigate = useNavigate();
  const [lastScrollY, setLastScrollY] = useState(0);
  const [showNav, setShowNav] = useState(true);
  const dropdownRef = useRef(null);

  useEffect(() => {
    const handleScroll = () => {
      const currentY = window.scrollY;
      setShowNav(currentY < lastScrollY || currentY < 50);
      setLastScrollY(currentY);
    };
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, [lastScrollY]);

  const handleProfileClick = () => {
    navigate('/profile');
  };

  return (
    <>
      <header className={`navbar ${showNav ? 'visible' : 'hidden'}`}>
        <div className="navbar-container">
          <div className="navbar-left">
            <Link to="/" className="brand">MelodyHub</Link>
          </div>

          <div className="navbar-center">
            <Link to="/items" className="nav-item">Events</Link>
            <Link to="/Add-Event" className="nav-item">Add Event</Link>
            <Link to="/artists" className="nav-item">Artists</Link>
          </div>

          <div className="navbar-right">
            {user ? (
              <button onClick={handleProfileClick} className="icon-button">
                {/* Simple user icon using emoji or you can replace with an SVG */}
                ðŸ‘¤
              </button>
            ) : (
              <Link to="/login" className="nav-item login-button">Login</Link>
            )}
          </div>
        </div>
      </header>

      <style>{`
        .navbar {
          position: fixed;
          top: 0;
          width: 100%;
          background-color: #000;
          color: #fff;
          z-index: 1000;
          transition: transform 0.3s ease;
          padding: 0 20px;
          height: 60px;
          display: flex;
          align-items: center;
        }
        .navbar.hidden {
          transform: translateY(-100%);
        }
        .navbar.visible {
          transform: translateY(0);
        }
        .navbar-container {
          width: 100%;
          max-width: 1200px;
          margin: 0 auto;
          display: flex;
          align-items: center;
          justify-content: space-between;
          position: relative;
        }
        .navbar-left {
          flex: 1;
          display: flex;
          align-items: center;
        }
        .navbar-center {
          position: absolute;
          left: 50%;
          transform: translateX(-50%);
          display: flex;
          gap: 24px;
        }
        .navbar-right {
          flex: 1;
          display: flex;
          justify-content: flex-end;
          align-items: center;
        }
        .brand {
          color: #fff;
          text-decoration: none;
          font-size: 1.5rem;
          font-weight: bold;
        }
        .nav-item {
          color: #fff;
          text-decoration: none;
          font-size: 1rem;
          transition: color 0.2s;
          background: none;
          border: none;
          cursor: pointer;
        }
        .nav-item:hover {
          color: #ddd;
        }
        .icon-button {
          background: none;
          border: none;
          color: #fff;
          font-size: 1.5rem;
          cursor: pointer;
        }
        .icon-button:hover {
          color: #ddd;
        }
        .login-button {
          font-size: 1rem;
          color: #fff;
          background: none;
          border: none;
          cursor: pointer;
          text-decoration: none;
        }
        @media (max-width: 768px) {
          .navbar-center {
            display: none;
          }
        }
      `}</style>
    </>
  );
};

export default Navbar;
