import React, { useState, useEffect, useRef } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';

const Navbar = ({ user, onLogout }) => {
  const navigate = useNavigate();
  const [dropdownOpen, setDropdownOpen] = useState(false);
  const [lastScrollY, setLastScrollY] = useState(0);
  const [showNav, setShowNav] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [mobileOpen, setMobileOpen] = useState(false);
  const dropdownRef = useRef(null);

  // hide on scroll
  useEffect(() => {
    const handleScroll = () => {
      const currentY = window.scrollY;
      setShowNav(currentY < lastScrollY || currentY < 50);
      setLastScrollY(currentY);
    };
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, [lastScrollY]);

  // close dropdown on outside click
  useEffect(() => {
    const handleClickOutside = (e) => {
      if (dropdownRef.current && !dropdownRef.current.contains(e.target)) {
        setDropdownOpen(false);
      }
    };
    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, []);

  const handleLogout = async () => {
    try {
      await axios.post('http://localhost:8000/logout', {}, { withCredentials: true });
      onLogout();
      navigate('/');
    } catch (err) {
      console.error('Logout failed:', err);
      alert('Logout failed. Please try again.');
    }
    setDropdownOpen(false);
    setMobileOpen(false);
  };

  const toggleDropdown = () => setDropdownOpen(prev => !prev);

  const handleSearch = (e) => {
    e.preventDefault();
    // perform search, e.g.: navigate(`/search?q=${encodeURIComponent(searchTerm)}`);
    setMobileOpen(false);
  };

  return (
    <>
      <header className={`navbar ${showNav ? 'visible' : 'hidden'}`}>
        <div className="navbar-content">
          <div className="navbar-left">
            <Link to="/" className="brand">
              <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" fill="currentColor" viewBox="0 0 16 16" className="cap-icon">
                <path d="M8 0L1 4l7 4 7-4-7-4zm0 8l-8-4v6l8 4 8-4V4l-8 4z"/>
              </svg>
              <span>MelodyHub</span>
            </Link>
          </div>

          <nav className="navbar-center">
            <Link to="/" className="nav-item">Home</Link>
            <Link to="/items" className="nav-item">Events List</Link>
            <Link to="/Add-Event" className="nav-item">Add Event</Link>
            <Link to="/artists" className="nav-item">Artists</Link>
          </nav>

          <div className="navbar-right">
            <form onSubmit={handleSearch} className="search-form">
              <input
                type="text"
                placeholder="Search"
                value={searchTerm}
                onChange={e => setSearchTerm(e.target.value)}
                className="search-input"
              />
              <button type="submit" className="icon-button" aria-label="Search">
                <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" viewBox="0 0 16 16">
                  <path d="M11.742 10.344a6.5 6.5 0 10-1.397 1.398h-.001l3.85 3.85a1 1 0 001.415-1.414l-3.85-3.85zm-5.242 1.106a5 5 0 110-10 5 5 0 010 10z"/>
                </svg>
              </button>
            </form>

            <button className="icon-button settings-button" aria-label="Settings">
              <svg xmlns="http://www.w3.org/2000/svg" width="20" height="20" fill="currentColor" viewBox="0 0 16 16">
                <path d="M8 4.754a3.246 3.246 0 100 6.492 3.246 3.246 0 000-6.492zM5.754 8a2.246 2.246 0 114.492 0 2.246 2.246 0 01-4.492 0z"/>
                <path d="M9.796 1.343c-.527-1.79-3.065-1.79-3.592 0l-.094.319a.873.873 0 01-1.255.52l-.292-.16c-1.64-.892-3.433.902-2.54 2.541l.159.292a.873.873 0 01-.52 1.255l-.319.094c-1.79.527-1.79 3.065 0 3.592l.319.094a.873.873 0 01.52 1.255l-.159.292c-.892 1.64.902 3.433 2.541 2.54l.292-.159a.873.873 0 011.255.52l.094.319c.527 1.79 3.065 1.79 3.592 0l.094-.319a.873.873 0 011.255-.52l.292.159c1.64.892 3.433-.902 2.54-2.541l-.159-.292a.873.873 0 01.52-1.255l.319-.094c1.79-.527 1.79-3.065 0-3.592l-.319-.094a.873.873 0 01-.52-1.255l.159-.292c.892-1.64-.902-3.433-2.541-2.54l-.292.159a.873.873 0 01-1.255-.52l-.094-.319zM10.93 10.93a1.5 1.5 0 11-2.121-2.121 1.5 1.5 0 012.12 2.12z"/>
              </svg>
            </button>

            <div className="user-dropdown" ref={dropdownRef}>
              {user ? (
                <>
                  <button className="icon-button user-button" onClick={toggleDropdown}>
                    More <span className="arrow">‚ñæ</span>
                  </button>
                  {dropdownOpen && (
                    <div className="dropdown-menu">
                      <Link to="/User" className="dropdown-item" onClick={() => setDropdownOpen(false)}>
                        Profile
                      </Link>
                      <button className="dropdown-item" onClick={handleLogout}>
                        Logout
                      </button>
                    </div>
                  )}
                </>
              ) : (
                <Link to="/login" className="nav-item login-button">Login</Link>
              )}
            </div>

            <button className="hamburger" onClick={() => setMobileOpen(!mobileOpen)}>
              {mobileOpen ? '‚úï' : '‚ò∞'}
            </button>
          </div>
        </div>

        {mobileOpen && (
          <div className="mobile-menu">
            <form onSubmit={handleSearch} className="mobile-search">
              <input
                type="text"
                placeholder="Search"
                value={searchTerm}
                onChange={e => setSearchTerm(e.target.value)}
                className="search-input"
              />
              <button type="submit" className="icon-button">
                üîç
              </button>
            </form>
            <Link to="/" onClick={() => setMobileOpen(false)} className="mobile-item">Home</Link>
            <Link to="/items" onClick={() => setMobileOpen(false)} className="mobile-item">Events List</Link>
            <Link to="/Add-Event" onClick={() => setMobileOpen(false)} className="mobile-item">Add Event</Link>
            <Link to="/artists" onClick={() => setMobileOpen(false)} className="mobile-item">Artists</Link>
            {user && <Link to="/User" onClick={() => setMobileOpen(false)} className="mobile-item">Profile</Link>}
            {user ? (
              <button onClick={handleLogout} className="mobile-item">Logout</button>
            ) : (
              <Link to="/login" onClick={() => setMobileOpen(false)} className="mobile-item">Login</Link>
            )}
          </div>
        )}
      </header>

      <style>{`
        .navbar {
          position: fixed;
          top: 0;
          left: 0;
          width: 100%;
          background: rgba(0, 0, 0, 0.6);
          color: #fff;
          z-index: 1000;
          transition: transform 0.3s ease;
        }
        .navbar.hidden { transform: translateY(-100%); }
        .navbar.visible { transform: translateY(0); }
        .navbar-content {
          max-width: 1200px;
          margin: 0 auto;
          display: flex;
          align-items: center;
          justify-content: space-between;
          height: 60px;
          padding: 0 20px;
        }
        .brand { display: flex; align-items: center; color: #fff; text-decoration: none; font-size: 1.25rem; font-weight: 500; }
        .cap-icon { margin-right: 8px; }
        .navbar-center { display: flex; gap: 24px; }
        .nav-item { color: #fff; text-decoration: none; font-size: 1rem; background: none; border: none; cursor: pointer; transition: color 0.2s; }
        .nav-item:hover { color: #ddd; }
        .navbar-right { display: flex; align-items: center; gap: 12px; }
        .search-form, .mobile-search { display: flex; align-items: center; margin-bottom: 8px; }
        .search-input { padding: 6px 12px; border-radius: 20px; border: none; outline: none; width: 180px; }
        .icon-button { background: none; border: none; color: #fff; cursor: pointer; padding: 4px; display: flex; align-items: center; font-size: 1rem; }
        .settings-button svg { fill: #fff; }
        .user-dropdown { position: relative; }
        .user-button { background: none; border: none; color: #fff; cursor: pointer; font-size: 1rem; display: flex; align-items: center; }
        .arrow { margin-left: 4px; }
        .dropdown-menu { position: absolute; right: 0; top: 100%; background: rgba(0,0,0,0.8); border: 1px solid #444; box-shadow: 0 2px 8px rgba(0,0,0,0.15); padding: 0.5rem 0; z-index: 100; min-width: 150px; }
        .dropdown-item { display: block; padding: 0.5rem 1rem; color: #fff; text-decoration: none; background: none; border: none; width: 100%; text-align: left; cursor: pointer; }
        .dropdown-item:hover { background: #333; }
        .hamburger { display: none; background: none; border: none; color: #fff; font-size: 1.5rem; cursor: pointer; }
        .mobile-menu { position: absolute; top: 60px; left: 0; width: 100%; background: rgba(0,0,0,0.6); padding: 12px 20px; box-shadow: 0 2px 8px rgba(0,0,0,0.5); }
        .mobile-item { display: block; padding: 10px 0; color: #fff; text-decoration: none; font-size: 1rem; }
        .mobile-item:hover { color: #ddd; }
        /* Font-size media adjustments */
        @media (max-width: 768px) {
          .nav-item, .icon-button, .search-input, button {
            font-size: 18px !important;
          }
          .navbar-center { display: none; }
          .hamburger { display: block; }
        }
