// Make sure to install dependencies:
import React, { useState, useEffect, useRef } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';

const Navbar = ({ user, onLogout }) => {
  const [dropdownOpen, setDropdownOpen] = useState(false);
    const [lastScrollY, setLastScrollY] = useState(0);
    const [showNav, setShowNav] = useState(true);
    const dropdownRef = useRef(null);
  

  // hide on scroll
  useEffect(() => {
    const handleScroll = () => {
      const currentY = window.scrollY;
      setShowNav(currentY < lastScrollY || currentY < 50);
      setLastScrollY(currentY);
    };
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, [lastScrollY]);

  const handleLogout = async () => {
    try {
      await axios.post('http://localhost:8000/logout', {}, { withCredentials: true });
      onLogout();
      navigate('/');
    } catch (err) {
      console.error(err);
      alert('Logout failed.');
    }
  };


  return (
    <>
      <header className={`navbar ${showNav ? 'visible' : 'hidden'}`}>
        <div className="navbar-content">
          <div className="navbar-left">
            <Link to="/" className="brand">
              {/* Inline graduation cap icon */}
              <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" fill="currentColor" viewBox="0 0 16 16" className="cap-icon">
                <path d="M8 0L1 4l7 4 7-4-7-4zm0 8l-8-4v6l8 4 8-4V4l-8 4z"/>
              </svg>
              <span>MelodyHub</span>
            </Link>
          </div>
          <nav className="navbar-center">
            <Link to="/items" className="nav-item">Events</Link>
            <Link to="/Add-Event" className="nav-item">Add Event</Link>
            <Link to="/artists" className="nav-item">Artists</Link>
          </nav>
            {user ? (
              <button onClick={handleLogout} className="nav-item logout-button">Logout</button>
            ) : (
              <Link to="/login" className="nav-item login-button">Login</Link>
            )}

        </div>
      </header>

      <style>{`
        .navbar {
          position: fixed;
          top: 0;
          left: 0;
          width: 100%;
          background-color: #000;
          color: #fff;
          z-index: 1000;
          transition: transform 0.3s ease;
        }
        .navbar.hidden {
          transform: translateY(-100%);
        }
        .navbar.visible {
          transform: translateY(0);
        }
        .navbar-content {
          max-width: 1200px;
          margin: 0 auto;
          display: flex;
          align-items: center;
          justify-content: space-between;
          height: 60px;
          padding: 0 20px;
        }
        .brand {
          display: flex;
          align-items: center;
          color: #fff;
          text-decoration: none;
          font-size: 1.25rem;
          font-weight: 500;
        }
        .cap-icon {
          margin-right: 8px;
        }
        .navbar-center {
          display: flex;
          gap: 24px;
        }
        .nav-item {
          color: #fff;
          text-decoration: none;
          font-size: 1rem;
          transition: color 0.2s;
          background: none;
          border: none;
          cursor: pointer;
        }
        .nav-item:hover {
          color: #ddd;
        }
        .navbar-right {
          display: flex;
          align-items: center;
          gap: 16px;
        }
        .search-form {
          display: flex;
          align-items: center;
        }
        .search-form input {
          padding: 6px 12px;
          border-radius: 20px;
          border: none;
          width: 180px;
          outline: none;
        }
        .icon-button {
          background: none;
          border: none;
          color: #fff;
          cursor: pointer;
          padding: 4px;
          display: flex;
          align-items: center;
        }
        .settings-button svg {
          fill: #fff;
        }
        .logout-button, .login-button {
          font-size: 1rem;
          color: #fff;
          background: none;
          border: none;
          cursor: pointer;
        }
        .login-button { text-decoration: none; }
        @media (max-width: 768px) {
          .navbar-center { display: none; }
          .search-form input { width: 120px; }
        }
      `}</style>
    </>
  );
};

export default Navbar;
