import React, { useState, useEffect, useRef } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';

const Navbar = ({ user, onLogout }) => {
  const [dropdownOpen, setDropdownOpen] = useState(false);
    const [lastScrollY, setLastScrollY] = useState(0);
    const [showNav, setShowNav] = useState(true);
    const dropdownRef = useRef(null);
  

  // hide on scroll
  useEffect(() => {
    const handleScroll = () => {
      const currentY = window.scrollY;
      setShowNav(currentY < lastScrollY || currentY < 50);
      setLastScrollY(currentY);
    };
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, [lastScrollY]);

  const handleLogout = async () => {
    try {
      await axios.post('http://localhost:8000/logout', {}, { withCredentials: true });
      onLogout();
      navigate('/');
    } catch (err) {
      console.error(err);
      alert('Logout failed.');
    }
  };


  return (
    <>
      <header className={`navbar ${showNav ? 'visible' : 'hidden'}`}>
        <div className="navbar-content">
          <div className="navbar-left">
            <Link to="/" className="brand">
              {/* Inline graduation cap icon */}
              <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" fill="currentColor" viewBox="0 0 16 16" className="cap-icon">
                <path d="M8 0L1 4l7 4 7-4-7-4zm0 8l-8-4v6l8 4 8-4V4l-8 4z"/>
              </svg>
              <span>MelodyHub</span>
            </Link>
          </div>
          <nav className="navbar-center">
            <Link to="/" className="nav-item">Home</Link>
            <Link to="/products" className="nav-item">Products</Link>
            <Link to="/features" className="nav-item">Features</Link>
            <Link to="/about" className="nav-item">About</Link>
          </nav>

            <button className="icon-button settings-button" aria-label="Settings">
              {/* Inline gear icon */}
              <svg xmlns="http://www.w3.org/2000/svg" width="20" height="20" fill="currentColor" viewBox="0 0 16 16">
                <path d="M8 4.754a3.246 3.246 0 100 6.492 3.246 3.246 0 000-6.492zM5.754 8a2.246 2.246 0 114.492 0 2.246 2.246 0 01-4.492 0z"/>
                <path d="M9.796 1.343c-.527-1.79-3.065-1.79-3.592 0l-.094.319a.873.873 0 01-1.255.52l-.292-.16c-1.64-.892-3.433.902-2.54 2.541l.159.292a.873.873 0 01-.52 1.255l-.319.094c-1.79.527-1.79 3.065 0 3.592l.319.094a.873.873 0 01.52 1.255l-.159.292c-.892 1.64.902 3.433 2.541 2.54l.292-.159a.873.873 0 011.255.52l.094.319c.527 1.79 3.065 1.79 3.592 0l.094-.319a.873.873 0 011.255-.52l.292.159c1.64.892 3.433-.902 2.54-2.541l-.159-.292a.873.873 0 01.52-1.255l.319-.094c1.79-.527 1.79-3.065 0-3.592l-.319-.094a.873.873 0 01-.52-1.255l.159-.292c.892-1.64-.902-3.433-2.541-2.54l-.292.159a.873.873 0 01-1.255-.52l-.094-.319zM10.93 10.93a1.5 1.5 0 11-2.121-2.121 1.5 1.5 0 012.12 2.12z"/>
              </svg>
            </button>

            {user ? (
              <button onClick={handleLogout} className="nav-item logout-button">Logout</button>
            ) : (
              <Link to="/login" className="nav-item login-button">Login</Link>
            )}

        </div>
      </header>

      <style>{`
        .navbar {
          position: fixed;
          top: 0;
          left: 0;
          width: 100%;
          background-color: #000;
          color: #fff;
          z-index: 1000;
          transition: transform 0.3s ease;
        }
        .navbar.hidden {
          transform: translateY(-100%);
        }
        .navbar.visible {
          transform: translateY(0);
        }
        .navbar-content {
          max-width: 1200px;
          margin: 0 auto;
          display: flex;
          align-items: center;
