import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './SpotifyArtistList.css';

const SpotifyArtistList = () => {
  const [searchQuery, setSearchQuery] = useState('');
  const [artists, setArtists] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const fetchArtists = async (query) => {
    if (!query.trim()) return;

    setLoading(true);
    setError('');
    try {
      const response = await axios.get(`/api/spotify/search?query=${query}`);
      setArtists(response.data);
    } catch (err) {
      console.error(err);
      setError('Failed to load artists.');
    } finally {
      setLoading(false);
    }
  };

  const handleSearch = (e) => {
    e.preventDefault();
    fetchArtists(searchQuery);
  };

  return (
    <div className="artist-container">
      <h2 className="artist-title">ðŸŽ¤ Search for Spotify Artists</h2>

      <form
  onSubmit={handleSearch}
  style={{
    textAlign: 'center',
    marginBottom: '2rem',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    gap: '8px', // nice small gap between input and button
    flexWrap: 'wrap', // for mobile responsiveness
  }}
>
  <input
    type="text"
    value={searchQuery}
    onChange={(e) => setSearchQuery(e.target.value)}
    placeholder="Enter artist name..."
    style={{
      padding: '0.4rem 0.8rem',
      borderRadius: '20px',
      width: '220px',
      border: '1px solid #1DB954',
      fontSize: '0.9rem',
    }}
  />
  <button
    type="submit"
    style={{
      padding: '0.4rem 0.8rem',
      backgroundColor: '#1DB954',
      color: '#fff',
      border: 'none',
      borderRadius: '20px',
      cursor: 'pointer',
      fontSize: '0.9rem',
      whiteSpace: 'nowrap',    // ðŸ›  prevents stretching
      width: 'auto',           // ðŸ›  button auto-size
      minWidth: 'auto',         // ðŸ›  no full width
    }}
  >
    Search
  </button>
</form>



      {loading && <p className="artist-message">Loading artists...</p>}
      {error && <p className="artist-message" style={{ color: 'red' }}>{error}</p>}

      <div className="artist-grid">
        {artists.map((artist) => (
          <div key={artist.id} className="artist-card">
            {artist.images[0] && (
              <img
                src={artist.images[0].url}
                alt={artist.name}
              />
            )}
            <h3 className="artist-name">{artist.name}</h3>
            <p className="artist-genres">
              {artist.genres.length > 0 ? artist.genres.join(', ') : 'Unknown genres'}
            </p>
            <p className="artist-popularity">ðŸ”¥ {artist.popularity}</p>
            <a
              href={artist.external_urls.spotify}
              target="_blank"
              rel="noopener noreferrer"
              className="artist-link"
            >
              View on Spotify â†’
            </a>
          </div>
        ))}
      </div>
    </div>
  );
};

export default SpotifyArtistList;
